example.code:
QUnit.test( "a test", function( assert ) {

	function square( x ) {
		return x * x;
	}

	var result = square( 2 );

	assert.equal( result, 4, "square(2) equals 4" );
});

QUnit.test( "a Promise-returning test", function( assert ) {
	assert.expect( 0 );

	var thenable = new Promise(function( resolve, reject ) {
		setTimeout(function() {
			resolve( "result" );
		}, 500 );
	});
	return thenable;
});

QUnit.test( "a Promise-returning test", function( assert, done ) {
	setTimetout(function() {
		assert.ok( this );
		done();
	}, 500 );
});
